function splitJSONObjects(str) {
  const results = [];
  let stack = [];     // 追踪括号类型
  let inString = false; // 是否在字符串中
  let escapeNext = false; // 下一个字符是否被转义
  let startIndex = 0; // 当前JSON对象起始位置
  let hasBracket = false; // 防止类似 "text" 的非JSON结构被误判

  for (let i = 0; i < str.length; i++) {
    const char = str[i];
    
    // 处理转义字符
    if (escapeNext) {
      escapeNext = false;
      continue;
    }

    if (char === '\\') {
      escapeNext = true;
      continue;
    }

    // 处理字符串边界（忽略转义后的引号）
    if (char === '"' && !escapeNext) {
      inString = !inString;
      continue;
    }

    // 非字符串中的括号处理
    if (!inString) {
      if (char === '{' || char === '[') {
        stack.push(char);
        hasBracket = true;
      } else if (char === '}' && stack[stack.length - 1] === '{') {
        stack.pop();
      } else if (char === ']' && stack[stack.length - 1] === '[') {
        stack.pop();
      }

      // 栈空时捕获完整JSON对象
      if (stack.length === 0 && hasBracket) {
        results.push(str.substring(startIndex, i + 1).trim());
        startIndex = i + 1;
        hasBracket = false;
      }
    }
  }

  // 处理可能存在的尾部无效数据
  const lastChunk = str.substring(startIndex).trim();
  if (lastChunk) {
    throw new Error(`Found trailing non-JSON data: ${lastChunk}`);
  }

  return results;
}

